# Write all HTML files
for filename, content in web_files.items():
    with open(os.path.join(dir_name, filename), 'w', encoding='utf-8') as f:
        f.write(content)

# Create a zip archive of the website folder
zip_filename = "healthwisdom_website.zip"
with zipfile.ZipFile(zip_filename, 'w', zipfile.ZIP_DEFLATED) as zipf:
    for root, dirs, files in os.walk(dir_name):
        for file in files:
            zipf.write(os.path.join(root, file), arcname=file)

zip_filename
